DROP TABLE IF EXISTS birthdays;CREATE TABLE birthdays (                           id INT AUTO_INCREMENT PRIMARY KEY,                           photo VARCHAR(250),                           name VARCHAR(250) NOT NULL,                           city VARCHAR(250),                           category VARCHAR(250),                           date DATE NOT NULL,                           email VARCHAR(250),                           phone VARCHAR(50),                           notes VARCHAR(255),                           enable_reminders BOOLEAN DEFAULT false,                           passed BOOLEAN DEFAULT false,                           preferencies VARCHAR(250));